INSERT INTO profil (user_id, firstname, lastname, description, phone, city, photo, cv, github, linkedin, is_active) VALUES
  (1, 'Jean', 'Dupuis', "Développeur backend expérimenté avec plus de 10 ans dans le domaine. J'ai travaillé sur divers projets complexes, utilisant des technologies comme Java, Python et SQL. Passionné par l'optimisation des bases de données et l'amélioration des performances des applications. Toujours à l'affût des nouvelles technologies pour intégrer les meilleures pratiques dans mes projets.", '0654321987', 'Paris', NULL, NULL, 'https://github.com/jeandupuis', 'https://linkedin.com/in/jeandupuis', TRUE),
  (2, 'Marie', 'Legrand', "Spécialiste en UI/UX avec une expertise approfondie dans la création d'interfaces utilisateur intuitives et esthétiques. J'ai collaboré avec des équipes internationales pour développer des produits numériques engageants. Ma passion réside dans l'amélioration de l'expérience utilisateur par des designs innovants et centrés sur l'utilisateur. Je suis également formatrice en design UX.", '0654321988', 'Lyon', NULL, NULL, 'https://github.com/marielegrand', 'https://linkedin.com/in/marielegrand', TRUE),
  (3, 'Pierre', 'Dubois', "Expert en cybersécurité, je protège les entreprises contre les cybermenaces depuis plus de 8 ans. Ma spécialité inclut l'évaluation des vulnérabilités, la gestion des incidents de sécurité et la mise en place de solutions de sécurité robustes. Je suis certifié CEH et CISSP, et j'aime partager mes connaissances à travers des conférences et des ateliers. Ma mission est de rendre le cyberespace plus sûr.", '0654321989', 'Marseille', NULL, NULL, 'https://github.com/pierredubois', 'https://linkedin.com/in/pierredubois', TRUE),
  (4, 'Lucie', 'Martin', "Data analyst avec 5 ans d'expérience dans l'analyse de données complexes pour fournir des insights précieux aux entreprises. J'utilise des outils comme Python, R et SQL pour transformer les données brutes en informations exploitables. Mon expertise inclut la visualisation des données et le développement de modèles prédictifs. Passionnée par le big data et l'intelligence artificielle, je suis toujours en quête de nouvelles méthodes pour améliorer l'analyse des données.", '0654321990', 'Toulouse', NULL, NULL, 'https://github.com/luciemartin', 'https://linkedin.com/in/luciemartin', TRUE),
  (5, 'Sophie', 'Bernard', "Développeur frontend passionnée avec une forte expérience en JavaScript, React et CSS. J'ai un œil pour le design et une passion pour créer des interfaces utilisateur fluides et réactives. J'aime travailler en étroite collaboration avec les équipes de design pour transformer les maquettes en sites web fonctionnels. Toujours curieuse des nouvelles tendances en développement web, je m'efforce de rester à jour avec les technologies émergentes.", '0654321991', 'Nice', NULL, NULL, 'https://github.com/sophiebernard', 'https://linkedin.com/in/sophiebernard', TRUE);
INSERT INTO company (name, head_office, description, sales_figure, size, company_values, contact_name, contact_phone, contact_mail, activity_area_id) VALUES
  ('Tech Solutions', 'Paris', 'Entreprise spécialisée dans le développement de solutions logicielles. Nous offrons des produits innovants qui améliorent la productivité et l\'efficacité des entreprises. Nos équipes travaillent constamment sur des technologies de pointe pour garantir des solutions performantes et sécurisées. Notre mission est d\'aider les entreprises à atteindre leurs objectifs grâce à des outils numériques de qualité. Nous proposons également des services de conseil pour accompagner nos clients dans la transformation digitale. Forts d\'une expérience solide dans le secteur, nous sommes reconnus pour notre expertise et notre engagement envers la satisfaction client. En investissant continuellement dans la recherche et le développement, nous restons à la pointe de l\'innovation pour offrir des solutions adaptées aux besoins évolutifs du marché.', 1000000, 50, 'Innovation, Qualité, Satisfaction Client', 'Alice Durand', '0123456789', 'alice.durand@techsolutions.com', 1),
  ('CloudNet', 'Lyon', 'Fournisseur de solutions de cloud computing. Nous proposons une gamme complète de services cloud pour répondre aux besoins de toutes les entreprises. Notre infrastructure sécurisée et fiable permet à nos clients de se concentrer sur leur cœur de métier. Avec des datacenters de dernière génération, nous assurons une disponibilité maximale et des performances optimales. Nous offrons également des services de migration cloud, de gestion et de support technique, garantissant une transition en douceur vers des environnements cloud. Nos solutions sont conçues pour être flexibles et évolutives, permettant aux entreprises de s\'adapter rapidement aux changements du marché. L\'engagement envers la satisfaction de nos clients et la sécurité de leurs données est au cœur de notre stratégie.', 2000000, 100, 'Fiabilité, Sécurité, Performance', 'Bob Martin', '0987654321', 'bob.martin@cloudnet.com', 7),
  ('DataStream', 'Marseille', 'Experts en big data et analytics. Nous aidons les entreprises à tirer parti de leurs données pour prendre des décisions éclairées. Nos solutions d\'analyse avancées permettent de transformer les données en informations exploitables. Nous offrons des services de consulting en data science pour aider nos clients à développer des stratégies basées sur les données. Nos outils de visualisation de données et nos plateformes d\'intelligence artificielle facilitent l\'extraction de connaissances approfondies. En travaillant avec des entreprises de divers secteurs, nous avons acquis une expérience précieuse qui nous permet de fournir des solutions personnalisées et efficaces. Nous croyons fermement que l\'analyse de données est la clé pour une croissance durable et un avantage concurrentiel.', 1500000, 75, 'Analyse, Précision, Expertise', 'Caroline Dupont', '0123498765', 'caroline.dupont@datastream.com', 9),
  ('SecureSoft', 'Toulouse', 'Leader en sécurité informatique. Nous protégeons les entreprises contre les menaces numériques grâce à nos solutions de sécurité avancées. Notre équipe d\'experts est dédiée à fournir des services de sécurité de premier ordre pour assurer la protection des données. Nous proposons une gamme complète de produits, allant des antivirus aux pare-feu, en passant par les systèmes de détection d\'intrusion et les solutions de sauvegarde. Notre centre de recherche en cybersécurité travaille en permanence pour anticiper et contrer les nouvelles menaces. En outre, nous offrons des formations en sécurité informatique pour sensibiliser et former les employés de nos clients aux meilleures pratiques. Notre objectif est de garantir la tranquillité d\'esprit à nos clients en assurant une protection continue et proactive de leurs systèmes.', 2500000, 120, 'Protection, Confiance, Innovation', 'David Leroy', '0678901234', 'david.leroy@securesoft.com', 8),
  ('FinTech Innovations', 'Nice', 'Société spécialisée dans les technologies financières. Nous développons des solutions innovantes pour le secteur financier, en mettant l\'accent sur la transparence et l\'efficacité. Nos produits aident les institutions financières à optimiser leurs opérations et à mieux servir leurs clients. Nous offrons des plateformes de paiement sécurisées, des outils de gestion des risques et des solutions de conformité réglementaire. Nos équipes travaillent en étroite collaboration avec nos clients pour comprendre leurs besoins spécifiques et créer des solutions sur mesure. En intégrant les dernières avancées technologiques, nous permettons à nos clients de rester compétitifs dans un marché en constante évolution. Nous nous engageons à promouvoir une culture de confiance et de transparence dans toutes nos interactions professionnelles, en plaçant l\'intérêt de nos clients au cœur de notre démarche.', 3000000, 90, 'Transparence, Efficacité, Confiance', 'Eve Laurent', '0678905678', 'eve.laurent@fintech.com', 18),
  ('MedTech Solutions', 'Bordeaux', 'Société qui se consacre à l\'innovation dans le domaine des technologies médicales. Nous développons des dispositifs médicaux avancés pour améliorer la qualité des soins et sauver des vies. Nos solutions couvrent une gamme de spécialités médicales, y compris la cardiologie, l\'orthopédie, et la neurochirurgie. En collaborant avec des professionnels de la santé, nous veillons à ce que nos produits répondent aux plus hauts standards de sécurité et d\'efficacité. Notre équipe de recherche et développement travaille sans relâche pour introduire de nouvelles technologies sur le marché. MedTech Solutions s\'engage à fournir des produits de qualité supérieure et à contribuer de manière significative à la santé publique.', 5000000, 200, 'Innovation, Qualité, Impact Social', 'Jean Moreau', '0678904321', 'jean.moreau@medtechsolutions.com', 24),
  ('GreenEnergy Co.', 'Nantes', 'Pionnier dans le secteur des énergies renouvelables. Nous fournissons des solutions durables pour réduire l\'empreinte carbone et promouvoir un avenir plus vert. Nos produits incluent des panneaux solaires, des éoliennes, et des systèmes de stockage d\'énergie. Nous travaillons avec des entreprises et des collectivités locales pour mettre en œuvre des projets d\'énergie propre à grande échelle. En mettant l\'accent sur l\'innovation et la durabilité, GreenEnergy Co. s\'efforce de transformer le paysage énergétique mondial. Notre engagement envers l\'environnement et la communauté guide toutes nos actions, et nous aspirons à un avenir où l\'énergie propre est accessible à tous.', 8000000, 300, 'Durabilité, Innovation, Engagement Communautaire', 'Paul Lefevre', '0612345678', 'paul.lefevre@greenenergy.com', 17),
  ('EduTech Innovations', 'Grenoble', ' Transforme l\'éducation avec des technologies de pointe. Nous développons des plateformes d\'apprentissage numérique et des outils pédagogiques pour améliorer l\'expérience éducative. Nos solutions sont utilisées par des écoles, des universités et des entreprises pour offrir des programmes de formation interactifs et engageants. En intégrant l\'intelligence artificielle et l\'analyse des données, nous créons des environnements d\'apprentissage personnalisés qui répondent aux besoins individuels des étudiants. EduTech Innovations est déterminé à rendre l\'éducation accessible et efficace pour tous, et à préparer les étudiants pour le monde de demain.', 4000000, 150, 'Innovation, Accessibilité, Excellence', 'Sophie Martin', '0645671234', 'sophie.martin@edutech.com', 11),
  ('AgriTech Solutions', 'Rennes', 'Leader dans le domaine de l\'agriculture intelligente. Nous fournissons des technologies avancées pour améliorer l\'efficacité et la durabilité des pratiques agricoles. Nos produits incluent des capteurs de sol, des drones d\'inspection, et des systèmes de gestion de ferme. En combinant l\'agronomie avec l\'intelligence artificielle, nous aidons les agriculteurs à optimiser leurs rendements et à réduire leur impact environnemental. Notre mission est de révolutionner l\'agriculture en rendant les technologies de pointe accessibles à tous les agriculteurs. AgriTech Solutions s\'engage à soutenir une agriculture durable et résiliente pour les générations futures.', 6000000, 250, 'Innovation, Durabilité, Support Agricole', 'Claire Dubois', '0654321789', 'claire.dubois@agritech.com', 20),
  ('UrbanDesign Co.', 'Strasbourg', 'Spécialiste dans la conception architecturale et le développement urbain. Nous créons des espaces qui améliorent la qualité de vie tout en respectant les principes de durabilité. Nos projets incluent des complexes résidentiels, des espaces commerciaux, et des infrastructures publiques. Nous travaillons en étroite collaboration avec les autorités locales et les communautés pour concevoir des environnements urbains fonctionnels et esthétiques. UrbanDesign Co. s\'efforce de combiner innovation, fonctionnalité et esthétique dans chaque projet. Notre objectif est de façonner les villes de demain tout en préservant leur patrimoine culturel et environnemental.', 7000000, 180, 'Innovation, Esthétique, Durabilité', 'Marc Lefort', '0698765432', 'marc.lefort@urbandesign.com', 15),
  ('FoodTech Innovations', 'Lille', 'Entreprise qui se consacre à la transformation de l\'industrie alimentaire avec des solutions technologiques. Nous développons des systèmes de production alimentaire automatisés et des applications de gestion pour les entreprises de restauration. Nos solutions permettent d\'optimiser les chaînes d\'approvisionnement, de réduire les déchets alimentaires et d\'améliorer la traçabilité des produits. En collaborant avec des chefs et des restaurateurs, nous proposons des outils qui répondent aux besoins du secteur. FoodTech Innovations vise à moderniser l\'industrie alimentaire en mettant l\'accent sur la durabilité, l\'efficacité et la qualité. Nous croyons en l\'avenir de l\'alimentation durable et travaillons à créer des innovations qui profitent à tous.', 4500000, 160, 'Innovation, Durabilité, Qualité', 'Nicolas Petit', '0601234567', 'nicolas.petit@foodtech.com', 24),
  ('SmartHome Systems', 'Toulon', 'Révolutionne le confort domestique avec des solutions de domotique avancées. Nous concevons et installons des systèmes intelligents pour automatiser les maisons, incluant l\'éclairage, la sécurité, le chauffage, et la gestion de l\'énergie. Nos produits sont conçus pour être faciles à utiliser et à intégrer dans les habitations existantes. Avec un focus sur l\'innovation et la simplicité, nous transformons la façon dont les gens interagissent avec leur environnement domestique. SmartHome Systems s\'engage à améliorer le confort et la sécurité des foyers tout en réduisant leur impact énergétique. Notre mission est de rendre la vie quotidienne plus facile et plus agréable grâce à la technologie.', 3500000, 140, 'Confort, Innovation, Simplicité', 'Laura Martin', '0612349876', 'laura.martin@smarthome.com', 13),
  ('BioTech Innovations', 'Nancy', 'Entreprise à la pointe de la biotechnologie, développant des solutions pour les industries pharmaceutiques et agroalimentaires. Nous nous spécialisons dans la recherche et le développement de produits biologiques, y compris des vaccins, des thérapies géniques, et des biopesticides. Nos laboratoires sont équipés des dernières technologies pour assurer des recherches précises et des résultats fiables. En collaboration avec des partenaires académiques et industriels, nous visons à faire avancer la science pour le bien-être de la société. BioTech Innovations s\'engage à améliorer la santé et l\'environnement à travers des innovations biotechnologiques de pointe. Notre vision est de contribuer à un avenir où les solutions biologiques sont la norme pour les défis de la santé et de l\'environnement.', 9000000, 250, 'Innovation, Santé, Durabilité', 'Pierre Renault', '0623456789', 'pierre.renault@biotech.com', 12),
  ('AeroSpace Dynamics', 'Brest', 'Acteur majeur dans l\'industrie aérospatiale, spécialisé dans la conception et la fabrication de composants aéronautiques. Nous fournissons des solutions innovantes pour les avions commerciaux et militaires, avec un accent sur la légèreté, la résistance, et l\'efficacité énergétique. Nos équipes de R&D travaillent en étroite collaboration avec les principaux fabricants d\'avions pour développer des technologies de pointe. AeroSpace Dynamics est reconnu pour son expertise technique et sa capacité à livrer des produits de haute qualité conformes aux normes strictes de l\'industrie aérospatiale. Notre mission est de contribuer à l\'évolution de l\'industrie aéronautique avec des innovations qui améliorent la sécurité, la performance et la durabilité des aéronefs.', 15000000, 400, 'Excellence, Innovation, Sécurité', 'Isabelle Lefebvre', '0634567890', 'isabelle.lefebvre@aerospace.com', 3),
  ('HealthCare Plus', 'Nice', 'Spécialiste dans la fourniture de services de santé à domicile. Nous proposons des soins infirmiers, des services de réhabilitation, et des soins palliatifs pour les patients nécessitant une assistance médicale continue. Nos équipes de professionnels de la santé sont dédiées à fournir des soins de qualité avec compassion et respect. HealthCare Plus s\'engage à améliorer la qualité de vie des patients en offrant des soins personnalisés adaptés à leurs besoins spécifiques. Notre objectif est de rendre les soins de santé accessibles et pratiques, tout en maintenant les plus hauts standards de qualité et de sécurité. Nous croyons que chaque patient mérite des soins attentionnés et professionnels, dans le confort de son domicile.', 2000000, 100, 'Compassion, Qualité, Accessibilité', 'Julien Dubois', '0645678901', 'julien.dubois@healthcareplus.com', 24);
